mapper.select:
SELECT * FROM UPLOAD_ENTRIES WHERE STATUS = -1
OR EXPIRES_AT < strftime('%s', 'now') || substr(strftime('%f', 'now'), 4)
LIMIT #{limit}

mapper.update:
UPDATE UPLOAD_ENTRIES
MODEL = #{model}, MIME = #{mime}, LENGTH = #{length}, SOURCE = #{source}, UPLOAD = #{upload}, UPLOAD_KEY = #{uploadKey}, STATUS = #{status}, EXPIRES_AT = #{expiresAt}, UPDATED_AT = strftime('%s', 'now') || substr(strftime('%f', 'now'), 4), VERSION = VERSION + 1
WHERE ENTRY_ID = #{entryId} AND VERSION = #{version}
